import puppeteer from 'puppeteer';

function delay(time = 10000) {
    console.log("------- DELAY PROSES");

    return new Promise(function(resolve) { 
        setTimeout(resolve, time)
    });
}

const browser = await puppeteer.launch({
    headless : false,
    args: [
        // `--disable-extensions-except=${EXTENSION_PATH}`,
        // `--load-extension=${EXTENSION_PATH}`,
        "--no-sandbox",
    ]
});

await delay();

const newPageList = await browser.pages();

const page = newPageList[0];

await page.goto("https://birdeye.so/token/HZ1JovNiVvGrGNiiYvEozEVgZ58xaU3RKwX8eACQBCt3?chain=solana");

const connectButton = await page
.waitForSelector("button[class='sc-ctKHVw ibiVyx']",{
    timeout : 30000
});

console.log("------- KLIK BUTTON");

await connectButton.click();

